(ns advent-2017-clojure.day7
  (:require [clojure.string :as str]))

(defn parse-line [text]
  (let [splits (str/split text #"\W+")
        children (drop 2 splits)]
    { :name (first splits), :weight (Integer/parseInt (second splits)), :children children}))

(defn parse-data-file [file]
  (map parse-line (str/split file #"\n")))

(defn bottom-program [programs]
  (let [names (map (fn [x] (:name x)) programs)
        children (reduce concat (map (fn [x] (:children x)) programs))]
    (first (clojure.set/difference (set names) (set children)))))

; Ugly ugly ugly, but I'm too tired to refactor this right now.  It works.
(defn expected-weight-to-balance [programs]
  (let [{leaves true, nodes false} (group-by #(empty? (get % :children)) programs)]
    (loop [orphans (apply merge (map (fn [x] {(:name x) (assoc x :total-weight (:weight x))}) leaves))
           parents nodes]
      (let [parent (first (filter
                            (fn [p] (every? (fn [child] (contains? orphans child))
                                            (:children p)))
                            parents))
            child-weights (map #(:total-weight (orphans %)) (:children parent))]
        (if (apply not= child-weights)
          (let [target-weight (first (keep #(when (> (second %) 1) (first %)) (frequencies child-weights)))
                bad-child (first (keep #(when (not= target-weight (:total-weight %)) %) (map #(orphans %) (:children parent))))
                sum-of-children (- (:total-weight bad-child) (:weight bad-child))
                ]
            (- target-weight sum-of-children)
            )
          (recur (merge orphans {(:name parent) (assoc parent :total-weight (+ (:weight parent) (reduce + child-weights)))})
                 (remove #{parent} parents)))))))

; Test cases

(assert (= {:name "pbga" :weight 66 :children ()}
           (parse-line "pbga (66)")))
(assert (= {:name "fwft" :weight 72 :children '("ktlj" "cntj" "xhth")}
           (parse-line "fwft (72) -> ktlj, cntj, xhth")))


(def SAMPLE_INPUT "pbga (66)\nxhth (57)\nebii (61)\nhavc (66)\nktlj (57)\nfwft (72) -> ktlj, cntj, xhth\nqoyq (66)\npadx (45) -> pbga, havc, qoyq\ntknk (41) -> ugml, padx, fwft\njptl (61)\nugml (68) -> gyxo, ebii, jptl\ngyxo (61)\ncntj (57)")
(def SAMPLE_NODES (parse-data-file SAMPLE_INPUT))
(assert (= "tknk" (bottom-program SAMPLE_NODES)))

